    private void Update()
    {
        timer -= Time.deltaTime;
    }
    void OnCollisionEnter (Collision Col)
    {
        if (Col.gameObject.name== &quot;Player&quot; &amp;&amp; timer &lt;= 0f)
        {
            Debug.Log(&quot;collision detected&quot;);

            Destroy(gameObject);

        }
