function GetRange(ss){
  var today = new Date().getDay();
  // assuming you&#39;re only making protected ranges on the first sheet
  var protections = ss.getSheets()[0].getProtections(SpreadsheetApp.ProtectionType.RANGE); 
  if (today == 0){ // sunday
