                    strcpy(one, &quot;1&quot;);
                    strcat(one, space);
                    strcpy(two, &quot;2&quot;);
                    strcat(two, space);
                    strcpy(three, &quot;3&quot;);
                    strcat(complete_row, one);
                    strcat(complete_row, two);
                    strcat(complete_row, three);
                    count++;    // increment count
                }
