        // http://warp.povusers.org/Mandelbrot/
        private int checkForBound (int maxIterations, double c_re, double c_im)
        {
            double z_re = c_re;
            double z_im = c_im;

            int n;

            for (n = 1; n &lt;= maxIterations; ++n)
            {
                double z_re2 = z_re * z_re;
                double z_im2 = z_im * z_im;

                if (z_re2 + z_im2 &gt; 4)
