    if (wxEmptyString == wxString::FromUTF8(sData))
    {
        sUTF8Data = sData.ToUTF8();
    }
    else
    {
        sUTF8Data = sData;
    }
    wxFFileOutputStream out(fileFullPath);

    wxBOM bomType = wxConvAuto::DetectBOM(sUTF8Data, sUTF8Data.size());
    if (wxBOM_UTF8 != bomType)
