    &gt; as.Date(d$date_time)
     [1] &quot;2018-07-08&quot; &quot;2018-07-09&quot; &quot;2018-07-01&quot; &quot;2018-07-02&quot; &quot;2018-07-03&quot; &quot;2018-07-02&quot; &quot;2018-07-03&quot;
