        })
    }
    
    func receivePutDataFor(_ command:String, parameters:AnyObject?, success:@escaping (_ json: AnyObject) -&gt; Void, failure:@escaping (_ json: AnyObject?) -&gt; ()) {
        return alamofireRequest(domain: putDomain + command, method: .put, parameters: parameters as! Parameters?,
                                success: {(json) in
                                    return success(json as AnyObject)
        },  failure: {(error) in
            failure(error as NSError)
        })
    }

