    onSelect={e =&gt; {
              const { start, end } = e;
              if (start &amp;&amp; end &amp;&amp; start.i === end.i &amp;&amp; start.j !== end.j) {
                const grid = this.state.grid.map((row, index) =&gt; {
                  if (index === start.i) {
                    row[end.j] = row[start.j];
                  }
                  return [...row];
                });
