        ShowMessage(&#39;Error.... &#39; + e.ClassName + &#39; &#39; + e.Message);
      end;
    end;
  end else
  begin
    Beep;
    Beep;
    ShowMessage(&#39;Please enter the id code.&#39;);
  end;
end;
