    public BigInteger add(BigInteger n){
        // Ensure that the length of str1 is smaller
        if (length &gt; n.length){
            BigInteger t = new BigInteger(number);
            setBigInteger(n.number);
            n = t;
        }
    
        // Empty string for storing result
        String str = &quot;&quot;;
    
        // Calculate the length of the two strings
        int d1 = length, d2 = n.length;
    
        // Reverse both of the strings
        String str1 = new StringBuilder(String.valueOf(number)).reverse().toString();
        String str2 = new StringBuilder(String.valueOf(n.number)).reverse().toString();
    
        int carry = 0;
        for (int i = 0; i &lt; d1; i++)
        {
            // Calculate the total of the current digits and carry over
            int sum = ((int)(str1.charAt(i) - &#39;0&#39;) +
                        (int)(str2.charAt(i) - &#39;0&#39;) + carry);
            str += (char)(sum % 10 + &#39;0&#39;);
    
            carry = sum / 10;
        }
    
        // Add the remaining larger digits
        for (int i = d1; i &lt; d2; i++)
        {
            int sum = ((int)(str2.charAt(i) - &#39;0&#39;) + carry);
            str += (char)(sum % 10 + &#39;0&#39;);
            carry = sum / 10;
        }
    
        // Add the remaining carry
        if (carry &gt; 0)
            str += (char)(carry + &#39;0&#39;);
    
        // Reverse the resultant string
        str = new StringBuilder(str).reverse().toString();
        BigInteger result = new BigInteger(str);    
        return result;
    }
  
    /* Returns &quot;true&quot; if str2 is greater than str1,
    else - returns it to be &quot;false&quot;.*/
     boolean isSmaller(String str1, String str2)
    {
        // Calculate lengths of both string
        int d1 = str1.length(), d2 = str2.length();
    
        if (d1&lt;d2)
            return true;
        if (d2&lt;d1)
            return false;
    
        for (int i = 0; i &lt; d1; i++)
        {
            if (str1.charAt(i) &lt; str2.charAt(i))
                return true;
            else if (str1.charAt(i) &gt; str2.charAt(i))
                return false;
        }
        return false;
    }
    
    
  
