 // Function for finding the difference
    String subFrom(String str1, String str2)
    {
        
        if (isSmaller(str1, str2))
        {
            String temp = str1;
            str1 = str2;
            str2 = temp;
        }
   
        // Another empty string for storing the result
        String str = &quot;&quot;;
    
       
        int d1 = str1.length(), d2 = str2.length();
        int diff = d1 - d2;
    
        
        int carry = 0;
    
        
        for (int i = d2 - 1; i &gt;= 0; i--)
        {
            // Perform the elementary school math for the difference of current digits and carry
            int sub = (((int)str1.charAt(i + diff) - (int)&#39;0&#39;) -
                        ((int)str2.charAt(i) - (int)&#39;0&#39;) - carry);
            if (sub &lt; 0)
            {
                sub = sub+10;
                carry = 1;
            }
            else
                carry = 0;
    
            str += String.valueOf(sub);
        }
    
        // subtract remaining digits of str1[]
        for (int i = d1 - d2 - 1; i &gt;= 0; i--)
        {
            if (str1.charAt(i) == &#39;0&#39; &amp;&amp; carry &gt; 0)
            {
                str += &quot;9&quot;;
                continue;
            }
            int sub = (((int)str1.charAt(i) - (int)&#39;0&#39;) - carry);
            if (i &gt; 0 || sub &gt; 0) // remove preceding 0&#39;s
                str += String.valueOf(sub);
            carry = 0;
    
        }
    
        // reverse resultant string
        return new StringBuilder(str).reverse().toString();
    }
      
  
