    String multiply(String num1, String num2)
    {
        int len1 = num1.length();
        int len2 = num2.length();
        if (len1 == 0 || len2 == 0)
            return &quot;0&quot;;
    
        // Keeps the result num in reverse order
        int solution[] = new int[len1 + len2];
    
        // Indexes
        int i_n1 = 0;
        int i_n2 = 0;
        
        for (int i = len1 - 1; i &gt;= 0; i--)
        {
            int carry = 0;
            int d1 = num1.charAt(i) - &#39;0&#39;;
    
            // Shift position left
            i_n2 = 0;
            
            // Go from right to left (num2)            
            for (int j = len2 - 1; j &gt;= 0; j--)
            {
                
                int d2 = num2.charAt(j) - &#39;0&#39;;
    
                // Multiply by the first number and add the result to the previous one
                int sum = d1 * d2 + solution[i_n1 + i_n2] + carry;
    
                carry = sum / 10;
                solution[i_n1 + i_n2] = sum % 10;
                i_n2++;
            }
    
            // stores the carry into the next cell
            if (carry &gt; 0)
                solution[i_n1 + i_n2] += carry;
    
            // Left shift position
            i_n1++;
        }
    
        // Ignore zeros from the right
        int i = solution.length - 1;
        while (i &gt;= 0 &amp;&amp; solution[i] == 0)
        i--;
    
        if (i == -1)
        return &quot;0&quot;;
    
        
        String s = &quot;&quot;;
        while (i &gt;= 0)
        s += (solution[i--]);
        return s;
    }
    
    
    public BigInteger divide(int n){
        String sol = division(String.valueOf(number),n);
        return new BigInteger(sol);
    }
  
    String division(String num, int div)
    {
        // Store the result in string
        String ans = &quot;&quot;;

        // Find prefix of num that is still larger than the divisor
        int idx = 0;
        char []number = num.toCharArray();
        int trans = number[idx] - &#39;0&#39;;

        while (trans &lt; div)
            trans = trans * 10 + (number[++idx] - &#39;0&#39;);
        
        idx +=1;
    
        // Repeat division with trans
        
        while (number.length &gt; idx)
        {
            // Store result in ans
            ans += (trans / div) ;
        
            // Take the next digit of number
            trans = (trans % div) * 10 + number[idx++] - &#39;0&#39;;
        }
    
        // If divisor is greater than number:
        if (ans.length() == 0)
            return &quot;0&quot;;
    
        // else return ans
        return ans;
    }
  
    
    int mod(String number, int a)
    {
        
        int sol = 0;
    
        // Take action on all digits of number
        for (int i = 0; i &lt; number.length(); i++)
             sol = (sol*10 + (int)this.number[i] - &#39;0&#39;) %a;
    
        return sol;
    }
