    val listOfPairs = data.map(_.split(&quot;,&quot;)).map { case Array(a, b) =&gt; a -&gt; b }
    
    val indexed = listOfPairs.zipWithIndex.map { case (tuple, idx) =&gt; idx -&gt; tuple }
    val lastIndex = indexed.count() - 1
    
    val newIndexed = indexed.map {
      case (idx, (a, b)) =&gt;
        if (idx == lastIndex)
          (0L, (a, b))
        else
          (idx + 1, (a, b))
    }
    
