        if (avgScore &lt;= 150 &amp;&amp; avgScore &gt;= 120)
          finalScore = &quot;A&quot;;
        else if (avgScore &lt; 120 &amp;&amp; avgScore &gt;= 100)
          finalScore = &quot;B&quot;;
        else if (avgScore &lt; 100 &amp;&amp; avgScore &gt;= 80)
