    &lt;div class=&quot;side-nav&quot; data-page=&#39;root&#39;&gt;
        &lt;div class=&quot;side-nav-button side-nav-active pointer&quot; id=&quot;dashboard-shortcut&quot; title=&quot;Home&quot; data-page=&quot;dashboard&quot;&gt;
