        mask[np.arange(len(remove_idx)), remove_idx] = False
        vd = np.dtype((np.void, a.dtype.itemsize * r))
        a_masked = as_strided(a[0,0,...].view(vd),a.shape[:-1],a.strides[:-1])[mask].view(a.dtype)
