    &lt;div style={containerStyle}&gt;
      {index !== &quot;&quot; &amp;&amp; &lt;h3 style={postHeaderStyle}&gt;Post #{index}&lt;/h3&gt;}
      &lt;ul&gt;
        &lt;li&gt;
          &lt;b&gt;userId:&lt;/b&gt; {data.userId}
        &lt;/li&gt;
