	printf(&quot;bufferFrameCount: %u, FrameSize_bytes: %d, current_period: %u\n&quot;, bufferFrameCount, FrameSize_bytes, current_period);

	hr = pAudioClient-&gt;GetService(
		IID_IAudioRenderClient,
