private Vector2 CalculaB_Spline(float t, Vector2 p0, Vector2 p1, Vector2 p2, Vector2 p3)
{
    //(t)=0&#215;B0(u−2)−1&#215;B1(u−2)+1&#215;B2(u−2)+2&#215;B3(u−2)
    float tt = t * t;
    float ttt = t * t * t;
    float u = 1 - t;
    float uuu = u * u * u;
    float q3 = uuu;
    float q2 = 3f * ttt - 6f * tt + 4f;
    float q1 =-3f * ttt + 3f * tt + 3f * t + 1f;
    float q0 = ttt;
    Vector2 p = (p0 * q3 + 
