   &lt;div class=&quot;container no_padding&quot;&gt;
     &lt;app-headcomp (myAction)=&quot;doSomething($event)&quot;&gt;&lt;/app-headcomp&gt;
   &lt;/div&gt;
 &lt;/div&gt;
