2.DLL.cpp(which is for header file)

    #include &quot;DoubleLinkedList.h&quot;

    Node* DLL_CreateNode(ElementType NewData)    
    {
	    //create node with memory allocation
	    Node* NewNode = (Node*)malloc(sizeof(Node));
    	
      //nullptr check for c6011(https://docs.microsoft.com/ko-kr/visualstudio/code-quality/c6011?)
	    if (NewNode)
	    {
	    	NewNode-&gt;Data = NewData;
	    	NewNode-&gt;Prev = NULL;
		    NewNode-&gt;Next = NULL;
	    }
	
	
	    return NewNode;
    }

    void DLL_DestroyNode(Node* Node)
    {
	    free(Node);
    }

    void DLL_AppendNode(Node** Head, Node* NewNode)
    {
	    if ((*Head) == NULL)
	    {
		    *Head = NewNode;
	    }
	    else
	    {
		    Node* Tail = (*Head);
		
    		while (Tail-&gt;Next != NULL)
	    	{
		    	Tail = Tail-&gt;Next;
		    } 
		    Tail-&gt;Next = NewNode;
		    NewNode-&gt;Prev = Tail;
	    }
    }

    void DLL_InsertAfter(Node* Current, Node* NewNode)
    {
   
	    NewNode-&gt;Prev = Current-&gt;Next;
	    NewNode-&gt;Next = Current;

	    if (Current-&gt;Next != NULL)
	    {
		    Current-&gt;Next-&gt;Prev = NewNode;
		    Current-&gt;Next = NewNode;
	    }
    }

    void DLL_RemoveNode(Node** Head, Node* Remove)
    {
	 
	    if (*Head == Remove)
	    {
		
		    *Head = Remove-&gt;Next;
		    if (*Head != NULL)
		    {
			    (*Head)-&gt;Prev = NULL;
		    }
		    Remove-&gt;Next = NULL;
		    Remove-&gt;Prev = NULL;
	    }
    	else
	    {
		    Node* Temp = Remove;

		    if (Remove-&gt;Prev != NULL)
		    {
			    
			    Remove-&gt;Prev-&gt;Next = Temp-&gt;Next;
		    }

		    if (Remove-&gt;Next != NULL)
		    {
		    	
		    	Remove-&gt;Next-&gt;Prev = Temp-&gt;Prev;
		    }
		   
	    	Remove-&gt;Prev = NULL;
		    Remove-&gt;Next = NULL;
	    }
    }

    Node* DLL_GetNodeAt(Node* Head, int location)
    {
	    Node* Current = Head;

	    while (Current != NULL &amp;&amp; (--location) &gt;= 0)
	    {
		    //--location &gt;=0 is for check index
		    Current = Current-&gt;Next;
	    }
    	return Current;
    }

    int DLL_GetNodeCount(Node* Head)
    {
	    int count = 0;

	    Node* Current = Head;

    	while (Current != NULL)
	    {
		    Current = Current-&gt;Next;
	    	count++;
	    }

	    return count;
    }


