        fn some_bool_function(&amp;self, i: usize) -&gt; bool {
            i%2==0
        }
        
        fn f&lt;&#39;b&gt;(&amp;&#39;b self) -&gt; impl Iterator&lt;Item = u8&gt; + &#39;b {
            (0..self.some_number())
