$res = array();

foreach($pages as $page){
    // Remove domain
    $url = preg_replace(&#39;/\/?(example\.com)/i&#39;, &#39;$2&#39;, $page[0]);
    
    // Remove query string
    $url = preg_replace(&#39;/(\/[^?]+).*/i&#39;, &#39;$1&#39;, $url);

    // Remove AMP
    $url = preg_replace(&#39;/\/amp\/\z|\/amp$/i&#39;, &#39;/&#39;, $url);

    if(array_key_exists($url, $res)){
        $res[$url] += $page[1];
    } else{
        $res[$url] = $page[1];
    }

}

