     &lt;div 
      v-if=&quot;isFullScreen&quot; 
      v-bind:class=&quot;{
        &#39;fullscreen&#39;:isFullScreen, 
        &#39;regularSize&#39;:!isFullScreen
     }&quot;&gt;	
		  &lt;p&gt;FULL SCREEN&lt;/p&gt;
