If your program is an exe file, you can use `CreateToolhelp32Snapshot` to enumerate the running process and retrieve me32.szExePath;

    #include &lt;Windows.h&gt;
    #include &lt;tlhelp32.h&gt;
    #include &lt;iostream&gt;
    #include &lt;tchar.h&gt;
    
    BOOL IsProcessRunning(TCHAR* process_name)
    {
    	BOOL boolean = 0;
    	PROCESSENTRY32 entry;
    	entry.dwSize = sizeof(PROCESSENTRY32);
    	HANDLE snapshot = CreateToolhelp32Snapshot(TH32CS_SNAPPROCESS, NULL);
    
    	if (!Process32First(snapshot, &amp;entry)) return 0;
    
    	do
    	{
    		if (_tcscmp(entry.szExeFile, process_name) == 0)
    		{
    			boolean = true;
    			break;
    		}
    	} while (Process32Next(snapshot, &amp;entry));
    
    	CloseHandle(snapshot);
    	return boolean;
    }
    
    int main()
    {
    	TCHAR process_name[] = _T(&quot;cmd.exe&quot;);
    	std::cout &lt;&lt; &quot;IsProcessRunning: &quot;&lt;&lt;IsProcessRunning(process_name)&lt;&lt; std::endl;
        return 0;
    }

