        public static IEnumerable&lt;IEnumerable&lt;TSource&gt;&gt; SplitByPredicate&lt;TSource&gt;(
            this IEnumerable&lt;TSource&gt; source,
            Func&lt;TSource, TSource, bool&gt; splitPredicate)
        {
