void column_mask_union(column_mask&amp; to, const column_mask&amp; with) {

    if (to.size() &gt; with.size()) {
        if (with.size()) {
            column_mask tmp = with;
            tmp.resize(to.size());
            to |= tmp;
        }
        return;
    }
    if (to.size() &lt; with.size()) {
