     12    select count(*) into tbl_exist_s2
     13      from all_tables
     14      where owner = &#39;SCOTT&#39; and table_name = &#39;SERV_REQ&#39;;
     15
     16      dbms_output.put_line(&#39;Cleanup run: &#39; || sysdate);
     17
     18    if (tbl_exist_s2 = 0) then
