        mMyDBHlpr.insertMovieAlternative(new MovieFav(&quot;The Magnificent Seven&quot;,&quot;Magnificent&quot;,&quot;2019-10-01&quot;,&quot;medium&quot;,&quot;//posters/movies/tms&quot;));
