  }
  elseif ($_ -in $maleNames) { # -in tests presence in the array, case-insensitively
    &quot;$_, you are a man.&quot;
  }
  elseif ($_ -in $femaleNames) {    
    &quot;$_, you are a woman.&quot;
  }
  else {
    &quot;$_, you have an odd name.&quot;
  }

}
