    function find(path, data) {
      function _search([key, ...rest], arr) {
        const found = (arr || []).find(o =&gt; o.name === key);
        return rest.length &amp;&amp; found ? _search(rest, found.child) : found;
      }
      return _search(path.split(&#39;.&#39;), data)
    }

    function extractVars(obj, vars = {}) {
      (obj || {}).var.forEach(v =&gt; vars[v.no] = v);
      (obj || {}).child.forEach(c =&gt; extractVars(c, vars));
      return vars;
