    echo array_sum(
             array_column(
                 array_filter(
                     $arrayjson,
                     function($row) {
                         return strpos($row[&#39;string&#39;], &#39;xxx&#39;) !== false;
                     }
                 ),
                 &#39;value&#39;
             )
         );

