        const client = new ConsumptionManagementClient(creds, subscriptionId);
        const expand = &quot;&quot;;
        const filter = &quot;&quot;;
        const skiptoken = &quot;&quot;;
        const top = 1000;
        const apply = &quot;&quot;;
        return client.usageDetails.list(expand, filter, skiptoken, top, apply).then((result) =&gt; {
