          dir: &#39;.tmp/scripts/lib&#39;,
          format: &#39;esm&#39;,
          name: &#39;lib&#39;,
          sourcemap: true,
          experimentalCodeSplitting: true,
          globals: {
            jquery: &#39;$&#39;,
