@tailrec
def findFirstBy[T](f: (T) =&gt; Boolean)(xs: Seq[T]): Option[T] = {
   xs match {
       case Seq() =&gt; None
       case Seq(head, _*) if f(head) =&gt; Some(head)
       case Seq(_, tail@_*) =&gt; findFirstBy(f)(tail)
   }
}

