function troca(){
	var file = document.getElementById(&quot;file&quot;);
	var nomeArq;
	var fullPath = file.value;
	if (fullPath) {
	   var startIndex = fullPath.indexOf(&#39;\\\\&#39;) &gt;= 0 ? fullPath.lastIndexOf(&#39;\\\\&#39;) : fullPath.lastIndexOf(&#39;/&#39;);
