        c2 = &#39;background-color: green&#39;
    
        df1 = pd.DataFrame(c1, index=x.index, columns=x.columns)
        m = x[&#39;Expiration Date&#39;] &lt; today
        df1 = df1.mask(m, c2)
        return df1

