          render={({ values, handleChange, setFieldValue }) =&gt; (
            &lt;Form&gt;
              &lt;FieldArray
                name=&#39;abonents&#39;
                render={arrayHelpers =&gt; {
                  if (!disabled) {
                    return (
                      &lt;textarea onChange={(e) =&gt; {
                        e.preventDefault()
                        setFieldValue(&#39;abonents&#39;, e.target.value.split(&#39;, &#39;))
