&lt;!-- language: lang-js --&gt;

    $(&quot;.input_focus input&quot;).keyup(function() {
      if (this.value.length == this.maxLength) {
        $(this).closest(&#39;li.input_focus&#39;).next(&#39;li.input_focus&#39;).find(&#39;input&#39;).focus();
      }
    });

&lt;!-- language: lang-html --&gt;

