        logManager.IHM_LOG_INFO(&quot;BEGIN HTTPRequestService loadExtLayout call pathToLoad=&quot;+JSON.stringify(pathToLoad));
    
        let loadResult = new Promise((resolve,reject)=&gt;{
            $.ajax({
                async:false,
                method: &quot;GET&quot;,
                url: pathToLoad
            }).done(function(result){
                    resolve(result);
