	if (argc &lt; 3)
		return (-1);
	search = argv[2];
    if (search[0] == &#39;\0&#39;)
        return (-1);
	if ((fp = fopen(argv[1], &quot;r&quot;)) == NULL)
		return (-1);
	int count = 0;
	while (fgets(buff, 1000, fp) != NULL)
	{
		char *temp = buff;
		while ((temp = strstr(temp, search)))
		{
			printf(&quot;%d. %s\n&quot;, count + 1, temp);
