            &lt;div className=&quot;modal-background&quot; onClick={handleClose} /&gt;
            &lt;div className=&quot;modal-content&quot; style={{ width: &quot;70vw!important&quot; }}&gt;
              &lt;p className=&quot;image &quot;&gt;{children}&lt;/p&gt;
            &lt;/div&gt;
            &lt;button
              className=&quot;modal-close is-large&quot;
              aria-label=&quot;close&quot;
              onClick={handleClose}
            /&gt;
          &lt;/div&gt;
        &lt;/div&gt;
      );
    };
