      {&quot;id&quot;:&quot;108&quot;,&quot;name&quot;:&quot;You are awesome!&quot;}
    ];

    var arr=[&quot;88&quot;,&quot;108&quot;];

    const filteredItems = items.filter(item =&gt; {
      if (arr.indexOf(item.id)&gt;-1) {
        return item
        }
