              saveToken(user.token);
              loginCompleter.complete(user);
            } else {
              loginCompleter.completeError(&quot;User not found&quot;);
            }
            _flutterWebviewPlugin.close();
          }
        });
    
     
    
        return loginCompleter.future;
      }
    
      void saveToken(token) async {
