def bisection(f,a,b,TOL):
    s = +1 if f(a)&lt;0 else -1;
    if s*f(b) &lt; 0: return &quot;Sign change condition violated&quot;
    while abs(b-a)&gt;TOL:
        c = 0.5*(a+b);
        if s*f(c) &lt; 0:
            a = c;
        else:
            b = c
    return 0.5*(a+b)

