        Set&lt;Integer&gt; missing = new HashSet&lt;&gt;();
        Set&lt;Integer&gt; store = new HashSet&lt;&gt;();
        int count = 0;
        for (int i = 0; i &lt; a.length; i++) {
            if (!store.contains(i + 1) &amp;&amp; i + 1 != a[i])
                missing.add(i + 1);
            if (i + 1 &lt; a[i])
                store.add(a[i]);
            else
                missing.remove(a[i]);
            if (missing.isEmpty())
                count++;
        }
        return count;
    }

