    // Mostly from https://stackoverflow.com/questions/6274339/how-can-i-shuffle-an-array
    const shuffle = (a) =&gt; {
      for (let i = a.length - 1; i &gt; 0; i--) {
        const j = Math.floor(Math.random() * (i + 1))
        ;[a[i], a[j]] = [a[j], a[i]]
