    while (matcher.find()) {
        spannable.setSpan(new ForegroundColorSpan(Color.WHITE), matcher.start(), matcher.end(), Spannable.SPAN_EXCLUSIVE_EXCLUSIVE);
    }
    
    // Matching digits followed by &quot;,&quot; 0 or 1 time
