    $pass = &quot;mypassword&quot;;
    try
    	$pdo = new \PDO(
                sprintf(
                    &quot;dblib:host=%s;dbname=%s&quot;,
                    $server,
                    $database
                ),
                $user,
                $pass
            );
    	$pdo-&gt;setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);
