      myStings.add(&quot;a&quot;);
      myStings.add(&quot;little&quot;);
      myStings.add(&quot;lamb&quot;);

    myStings = myStings.stream().map(x -&gt; x.toLowerCase()).collect(Collectors.toList());
    List&lt;String&gt; sortedList = myStings.stream().sorted().collect(Collectors.toList());

    System.out.println(&quot;Unsorted list: &quot; + myStings);
